import './reserve.css'
import useFetch from '../../hooks/useFetch'

import React, { useContext, useState } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCircleXmark } from '@fortawesome/free-solid-svg-icons'
import { SearchContext } from '../../context/SearchContext'

const Reserve = ({setOpen, hotelId}) => {
  const [selectedRooms, setSelectedRooms] = useState([])
  const {data, loading, error} = useFetch(`http://localhost:8800/api/hotels/room/${hotelId}`)
  const  {dates} = useContext(SearchContext)

  const getDatesInRange = (startDate, endDate) => {
    const start = new Date(startDate)
    const end = new Date(endDate)
    const date = new Date(start.getTime())

    let list = []
   while(date <= end) {
     list.push(new Date(date).getTime())
     date.setDate(date.getDate() + 1)
   }

   return list
  }

  console.log(getDatesInRange(dates[0].startDate, dates[0].endDate))

  const handleSelect = (e) => {
    const checked = e.target.checked
    const value = e.target.value
    setSelectedRooms(checked ? [...selectedRooms, value] : selectedRooms.filter(item => item !== value))
  }

  const handleClick = () => {

  }

  return (
    <div className='reserve' >
   <div className="rContainer">
     <FontAwesomeIcon icon={faCircleXmark } className='rClose' onClick={ () => setOpen(false)} />
     <span>
       Select your room:
     </span>
     {data.map(item => (
       <div className="rItem">
         <div className="rItemInfo">
           <div className="rTitle">{item.title}</div>
           <div className="rDesc">{item.desc} </div>
           <div className="rMax">Max People : <b>{item.maxPeople} </b> </div>
           <div className="rPrice">{item.price} </div>
         </div>
           {item.roomNumbers.map(roomNumber => (
         <div className="room">
             <label>{roomNumber.number} </label>
             <input type="checkbox" value={roomNumber._id}  onChange={handleSelect}/>
         </div>
           ))}
           <button className='rButton' onClick={handleClick}>Reserve now!</button>
       </div>
     ) )}

   </div>
    </div>
  )
}

export default Reserve
